import numpy as np
import math


class Binary(object):
    def __init__(self):
        self.vt = 0
        self.m = 0
        self.p = []

    def Binary_Search_Identification(self, Nt):
        """
        Using the idea of binary search, recursively perform binary identification on Nt
        until all illegal signatures are found.
        """
        # print(Nt)

        if np.sum(Nt) == 0 or len(Nt) == 1:
            # self.p.append(0)
            self.vt = self.vt + 1

        else:
            self.vt = self.vt + 1
            low = 0
            high = len(Nt)
            mid = math.ceil((low + high) / 2)
            Left_Nt = Nt[low:mid]
            Rigt_Nt = Nt[mid:high]
            # self.p.append(2)
            self.Binary_Search_Identification(Left_Nt)
            self.Binary_Search_Identification(Rigt_Nt)


